INSERT INTO `products` (`id`, `brand_id`, `category_id`, `sub_category_id`, `sub_sub_category_id`, `tags`, `vendor_id`, `supplier_id`, `unit_id`, `campaing_id`, `name_en`, `name_bn`, `slug`, `product_code`, `unit_weight`, `purchase_price`, `is_wholesell`, `wholesell_price`, `wholesell_minimum_qty`, `regular_price`, `discount_price`, `discount_type`, `minimum_buy_qty`, `stock_qty`, `low_qty`, `previous_stock`, `product_thumbnail`, `product_pdf`, `short_description_en`, `short_description_bn`, `description_en`, `description_bn`, `attributes`, `is_varient`, `attribute_values`, `variations`, `is_featured`, `is_deals`, `status`, `is_approved`, `is_digital`, `top_selling`, `package_book`, `show_stock`, `created_by`, `created_at`, `updated_at`) VALUES
(96, NULL, 18, NULL, NULL, NULL, 147, 29, 0, NULL, 'Paradoxical sajid Part -1', 'প্যারাডক্সিক্যাল সাজিদ -১', 'Paradoxical sajid-1', '19741', NULL, 180, 0, 0, 0, 225, 0, 1, 1, 7, 3, 9, 'upload/products/thumbnails/1803095936389842.jpg', '', NULL, NULL, NULL, '<p style=\"margin-top: 6pt;\"><span lang=\"EN\" style=\"line-height: 115%;\">ফ্ল্যাপ\r\n:</span><span lang=\"EN\" style=\"line-height: 115%;\"> </span><span lang=\"EN\" style=\"line-height: 115%;\">নাস্তিক আর সংশয়বাদীদের দিক থেকে</span><span lang=\"EN\" style=\"line-height: 115%;\"> </span><span lang=\"EN\" style=\"line-height: 115%;\">&nbsp;ইসলাম নিয়ে তোলা মৌলিক প্রশ্ন হাতে গোনা। বেশিরভাগ\r\nপ্রশ্নই ইসলাম-বিদ্বেষী, ঘৃণা আর প্রান্তিকতার চাদরে মোড়ানো। হাল জামানায় উত্থিত এসব\r\nপ্রশ্ন নতুন নয়, ১৪শ বছর আগেও ছিল; জিইয়ে আছে আজতক। এসব নিয়ে অতীতেও প্রচুর কাজ হয়েছে\r\nকিন্তু কোথাও যেন একটা শূনতা ছিল!</span><span lang=\"EN\" style=\"line-height: 115%;\"> </span><span lang=\"EN\" style=\"line-height: 115%;\">&nbsp;গিয়ে এলেন নতুন প্রজন্মের প্রতিশ্রুতিশীল কথাসৈনিক\r\nআরিফ আজাদ। পাঠকদের সামনে উপস্থাপন করলেন এক বিশ্বাসী চরিত্র ‘সাজিদ’।</span><span lang=\"EN\" style=\"line-height: 115%;\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top: 6pt;\"><span lang=\"EN\" style=\"line-height: 115%;\">আরিফ\r\nআজাদ একান্ত নিজের মতো করে অবিশ্বাসীদের জবাব দিয়েছেন। সাহিত্যের প্রচলিত গাম্ভীর্যতা\r\nপরিহার করে গল্পোচ্ছলে মিথ্যার ফানুস উড়িয়ে দিয়েছেন।প্যারাডক্সিক্যাল সাজিদ পাঠক-মানসে\r\nসত্যের বীজ বুনছে যুক্তির আকরে; কিছুটা আকর্ষণীয় ঢঙে, খুব সহজ ও সাবলীল ভাষায়। লাখো\r\nপাঠক প্যারাডক্সিক্যাল সাজিদ নিয়ে কথা বলছে, কিন্তু কেন? চলুন, পড়ে দেখি</span><span lang=\"EN\" style=\"line-height: 115%;\">…</span></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 03:18:04', '2024-06-30 07:55:44'),
(97, NULL, 19, NULL, NULL, NULL, 232, 22, 0, NULL, 'Office Dokkhoter 30 Mantra', 'অফিস দক্ষতার ৩০ মন্ত্র', 'Office-Dokkhoter-30-Mantra', '13473', NULL, 130, 0, 0, 0, 250, 20, 2, 1, 10, 5, 10, 'upload/products/thumbnails/1803096702605144.jpg', '', NULL, '<p><br></p>', NULL, '<p><span style=\"font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve; background-color: rgb(237, 237, 232);\"><font color=\"#424242\">প্রতিনিয়ত পরিবর্তন হচ্ছে বিশ্ব। কৌশলগত পরিকল্পনা করতে হচ্ছে প্রত্যেক সপ্তাহে। আবার পরিস্থিতি বুঝে পরিকল্পনা পাল্টাতেও হচ্ছে। আমাদেরকে মনে রাখতে হবে আমরা এখন ভ্যোকা (VUCA) ওয়ার্ল্ডে বাস করছি। V ফর Volatile, U ফর Uncertainty, C ফর Coplex এবং A ফর Ambiguity। তার মানে হলো, বর্তমান সময়টাই হচ্ছে অমসৃণ, বন্ধুর। আপনি কদাচিত স্বাচ্ছন্দময় গতি পাবেন। সর্বত্র অনিশ্চয়তা আর জটিলতা। কোনোকিছুই যেন স্বচ্ছ নয়। যা দেখছেন সত্যটা হয়তো তা নয়। কোনটা আসল, কোনটা মরীচিকা— সেটা বোঝা অন্য যেকোনো সময়ের তুলনায় এখন অনেক কঠিন। তাই আপনি যদি এই ভ্যোকা ওয়ার্ল্ডের চ্যালেঞ্জকে মোকাবিলা করতে চান তাহলে আপনাকে প্রতিনিয়ত চোখ কান খোলা রেখে, মেধা ও দক্ষতার সর্বোচ্চ ব্যবহার ঘটিয়ে এগিয়ে যেতে হবে। আর এই যাত্রা পথে এ বইটি হতে পারে আপনার সবচেয়ে বড় পরামর্শদাতা।</font></span><br></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 03:30:15', '2024-07-07 01:20:01'),
(98, NULL, 19, NULL, NULL, NULL, 199, 15, 0, NULL, 'Emotional Intelligence And Leadership', 'ইমোশনাল ইন্টেলিজেন্স এন্ড লিডারশীপ', 'emotional-intelligence-and-leadership', '40203', NULL, 270, 0, 0, 0, 400, 20, 2, 1, 9, 5, 10, 'upload/products/thumbnails/1803097073604208.jpg', '', NULL, '<p style=\"font-family: &quot;Times New Roman&quot;, MainFont; font-size: 1.4rem; margin-bottom: 0px; color: rgb(119, 119, 119); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal;\"><br></p>', NULL, '<p><ul><li style=\"font-family: &quot;Times New Roman&quot;, MainFont; font-size: 1.4rem; color: rgb(119, 119, 119); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal;\"><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\"> সর্বশক্তিমান আল্লাহ মানুষকে এই পৃথিবীর শ্রেষ্ঠ জীব হিসেবে সৃষ্টি করেছেন। বিবর্তনের ভেতর দিয়ে গুহামানব থেকে সভ্যতার এই পর্যায়ে মানুষ এসেছে শুধুমাত্র একটি জিনিসের ব্যবহার করে। আর সেটি হচ্ছে আবেগ। আবেগ মানুষের কল্পনার বিকাশ ও বাস্তবায়ন ঘটায়। বিবর্তনের এই ধারায় যে নেতারা অগ্রগামী ভূমিকা রেখেছেন তারা সবাই এই আবেগকে ব্যবহার করেছেন যথাযথভাবে। সুখ, দুঃখ, রাগ, ভয়, বিস্ময়, বিতৃষ্ণা, শঙ্কা বা বিশ্বাস প্রতিটি আবেগই আমাদের মাঝে শক্তি এবং তথ্যের আদান-প্রদান করে। </span><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\"> সফল নেতারা সেই শক্তি এবং তথ্যকে সঠিকভাবে ব্যবহার করে তাদের নেতৃত্বকে সুসংহত করেছেন যুগে যুগে। বর্তমান শতাব্দীর অন্যতম নেতৃ্ত্ব বিশেষজ্ঞ ওয়ারেন জি বেনিস বলছেন, “আমাদের বিশ্বের চারটি প্রধান হুমকির মধ্যে। প্রথমটি হল আমাদের মানব সমাজের বর্তমান নেতৃত্বের অবস্থা। শুধুমাত্র অনুকরণীয় নেতৃত্বই অন্য তিনটি মূল হুমকির সমাধান করতে পারেঃ (১) পারমাণবিক বা জৈবিক বিপর্যয়, (২) মহামারী এবং (৩) জাতিগত বিভাজন।“ তিনি আবার নেতৃ্ত্বের সবগুলো মডেলের মধ্যে আবেগীয় বুদ্ধিমত্তার মডেলকে সর্বোত্তম মডেল হিসেবে আখ্যায়িত করেছেন। আবেগীয় বুদ্ধিমত্তার নেতৃত্ব আপনার ব্যক্তিগত এবং পেশাগত জীবনকে কিভাবে সমৃদ্ধ করতে পারে সেই তথ্যভাণ্ডার দিয়ে খুব সহজ বাংলায় লিখা হয়েছে এই বইটি।</span><br></li></ul></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 03:36:09', '2024-07-10 08:24:02'),
(99, NULL, 21, NULL, NULL, NULL, 233, 29, 0, NULL, 'Koyekti Golpo', 'কয়েকটি গল্প', 'Koyekti-Golpo', '17887', NULL, 96, 0, 0, 0, 120, 0, 1, 1, 10, 3, 10, 'upload/products/thumbnails/1803097732350546.jpg', '', NULL, NULL, NULL, '<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"EN\" style=\"font-size:12.0pt;\r\nline-height:115%;font-family:&quot;Vrinda&quot;,sans-serif;mso-fareast-font-family:Vrinda\">ফ্ল্যাপ\r\n: </span></b><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:\r\n&quot;Arial Unicode MS&quot;;mso-fareast-font-family:&quot;Arial Unicode MS&quot;;mso-bidi-font-family:\r\n&quot;Arial Unicode MS&quot;\">মানুষ স্বপ্ন দেখে প্রতিনিয়ত। স্বপ্নের পেছনে ছুটতে গিয়ে পুরো\r\nজীবন অতিবাহিত করে। আকাশ ছোঁয়ার স্বপ্নে কেউ জীবনের প্রতিটি স্তর সৌখিনতায় সাজিয়ে\r\nতোলে। আবার কেউ-বা শুধুই দুবেলা পেট ভরে খাবার খেতে পাওয়ার স্বপ্ন দেখে। স্বপ্ন দুটোই,\r\nকিন্তু পার্থক্য বিশাল। প্রত্যেকটা স্বপ্নই যার যার কাছে অনেক বড়ো।সেই স্বপ্নের ধারাবাহিকতায়\r\nজীবনের অনেকটা পথ পেরিয়ে আসে কেউ অসংখ্য বাধা অতিক্রম করে। আবার কেউ-বা বাধাহীনভাবে\r\nস্বপ্ন পূরণ করে অবলীলায়। পৃথিবীর হাজারো পথ-পরিক্রমার মধ্যে দিয়ে কিছু ভিন্ন ভিন্ন\r\nস্বপ্ন পূরণের চেষ্টা নিয়ে তৈরি হয়েছে ‘কয়েকটি গল্প’।</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%\"><o:p></o:p></span></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 03:46:37', '2024-06-28 03:46:37'),
(100, NULL, 19, NULL, NULL, NULL, 0, 22, 0, NULL, 'executive to executive director', 'এক্সেকিউটিভ টু এক্সেকিউটিভ ডিরেক্টর', 'executive-to-executive-director', '88218', NULL, 290, 0, 0, 0, 400, 20, 2, 1, 8, 5, 8, 'upload/products/thumbnails/1803097765818271.jpg', '', NULL, '<p><span style=\"color: rgb(119, 119, 119); font-family: &quot;Times New Roman&quot;, MainFont; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 300;\">কর্পোরেটে কর্মরত, চাকরি প্রত্যাশী, কলেজ-বিশ্ববিদ্যালয়ের ছাত্র-ছাত্রীদের জন্য বইটি সাফল্য অর্জনের পাথেয় হিসেবে কাজ করবে।</span><br></p>', NULL, '<p style=\"font-family: &quot;Times New Roman&quot;, MainFont; font-size: 1.4rem; margin-bottom: 0px; color: rgb(119, 119, 119); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal;\"><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\">আমার পেশা জীবনের সাথে এই বইয়ের নামকরণের শতভাগ মিল আছে। আমি এক্সিকিউটিভ হিসেবে কর্ম জীবনে প্রবেশ করেছিলাম এবং এক্সিকিউটিভ ডিরেক্টর হিসেবে কর্ম জীবনের সমাপ্তি টেনেছি। </span><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\"> এদেশের অসংখ্য শিক্ষিত বেকার, ক্রমাগত শিক্ষা সমাপন করে পেশা জীবনে প্রবেশ করা ইয়ং জেনারেশন, কর্পোরেটে কর্মরত উচ্চাকাঙ্ক্ষী সহকর্মী ও অসংখ্য উদ্যোক্তার জন্য মূলত এই বইটি লেখা। </span><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\"> এক্সিকিউটিভ বা এক্সিকিউটিভ ডিরেক্টর কোনোটাই হওয়ার যোগ্যতা আমার ছিল না। আমি ইংরেজি সাহিত্য ও তুলনামূলক ধর্মতত্ত্বের ছাত্র। আমার অন্যতম পজিটিভ দিক হচ্ছে কোনো কাজ করতে যেয়ে বাধার সম্মুখীন হলেই আমার ভিতরে এক বল্গাহারা, অপ্রতিরোধ্য মানসিক ও শারীরিক শক্তির যুগলবন্দী শুরু হয়। শুরু হয় বাধা ডিঙানোর খেলা। </span><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\"> আমি বিজ্ঞানের ছাত্র ছিলাম না। কিন্তু জীবনের প্রথম চাকুরী ফাইজারের মতো আমেরিকান বহুজাতিক ঔষধ কোম্পানিতে নিতে যেয়ে আমাকে বিজ্ঞানের অনেক কিছু শুরু থেকে শিখতে হয়েছে। সন্ধ্যায় বই নিয়ে বসেছি আর সকালের সূর্য ওঠা দেখেছি জানালা দিয়ে। কঠিন জিদ নিয়ে ট্রেনিং করেছি যাতে শত শত প্রার্থীর মধ্যে একজনকে নিলেও যেন ফাইজার আমাকে নির্বাচিত করে। শেষ পর্যন্ত নির্বাচিত চার জনের মধ্যে আমি ছিলাম একজন। </span><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\"> আমাদের সময়টা ছিল ফার্মাসিউটিকাল-এর স্বর্ণযুগ। জ্ঞান নির্ভর ছিল সেই পেশা। যে যত পেশাগত জ্ঞানে উৎকর্ষ লাভ করতে পারবে সে তত শক্ত ভীত তৈরী করে উন্নতি লাভ করতে পারবে। আমার পরের চ্যালেঞ্জ ছিল অটোমোবাইল। রানার মোটরস-এ চাকরি নিয়ে দিল্লিতে গেলে ভলভো-আইশারের ইঞ্জিনিয়ার বলেছিলো \"how come a pharma guy in Automobile..... !!\" আমি মনে মনে বলেছিলাম দেখা হবে কিছুদিন পর। প্রমাণ হয়ে গিয়েছিল আমি আমার টিম নিয়ে কিভাবে অটোমোবাইলে পরিবর্তনের ঢেউ এনেছিলাম। পরের চ্যালেঞ্জ ছিল Akij Food and Beverage এর এক্সিকিউটিভ ডিরেক্টর হিসেবে।</span><br></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 03:47:09', '2024-07-04 02:54:30'),
(101, NULL, 18, NULL, NULL, NULL, 235, 29, 0, NULL, 'Halal Binodana', 'হালাল বিনোদন', 'Halal Binodana', '69852', NULL, 96, 0, 0, 0, 120, 0, 1, 1, 9, 3, 10, 'upload/products/thumbnails/1803098401146531.jpg', 'upload/products/pdf/1803098401225036.pdf', NULL, NULL, NULL, '<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"EN\" style=\"font-size:12.0pt;\r\nline-height:115%;font-family:&quot;Vrinda&quot;,sans-serif;mso-fareast-font-family:Vrinda\">ফ্ল্যাপ\r\n: </span></b><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:\r\n&quot;Arial Unicode MS&quot;;mso-fareast-font-family:&quot;Arial Unicode MS&quot;;mso-bidi-font-family:\r\n&quot;Arial Unicode MS&quot;\">এটা হারাম। ওটা হারাম। এটা করা যাবে না। ওটা করা যাবে না। তাহলে\r\nকি ইসলামে বিনোদন বলে কিছু নেই? হ্যাঁ, আছে। বিনোদনের শত শত মাধ্যম। কিছু ভালো, কিছু\r\nখারাপ। খারাপটা থেকে যদি ভালোটা আলাদা করতে না পারি, তাহলে নিছক আনন্দও হয়ে উঠবে শোচনীয়\r\nপরিণতির কারণ। কাজেই, সাবধান হওয়া জরুরি। বন্যার পানির মতো বিনোদনের হাজারো উপকরণ আসছে।\r\nজায়গা করে নিচ্ছে হাতের মুঠোয়। পানি জীবন ধারণের জন্য অপরিহার্য। কিন্তু সেই পানি যদি\r\nবিশুদ্ধ না হয়, ফোটানো না হয়, তাহলে সেটাই হয়ে উঠতে পারে জীবনঘাতি।</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Times New Roman&quot;,serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;\"> <o:p></o:p></span></p><p>\r\n\r\n</p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial Unicode MS&quot;;\r\nmso-fareast-font-family:&quot;Arial Unicode MS&quot;;mso-bidi-font-family:&quot;Arial Unicode MS&quot;\">এই\r\nবইতে আমরা আপনাকে একটা ফিল্টার দেবো। এই ফিল্টারে করে আপনি নিজেই ভালো আর খারাপ বিনোদনের\r\nউপকরণগুলো আলাদা করতে পারবেন। আনন্দের পরিশুদ্ধ মাধ্যমগুলো নিয়ে মেতে উঠুন হালাল বিনোদনে...</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%\"><o:p></o:p></span></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 03:57:15', '2024-07-05 02:18:14'),
(102, NULL, 18, NULL, NULL, NULL, 236, 29, 0, NULL, 'Prosnouttare siratunaabi', 'প্রশ্নোত্তরে সিরাতুন্নবি ﷺ', 'Prosnouttare-siratunaabi', '92976', NULL, 256, 0, 0, 0, 320, 0, 1, 1, 10, 3, 10, 'upload/products/thumbnails/1803098729277067.jpg', '', NULL, '<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; text-align: left; white-space-collapse: preserve;\"> </span><br></p>', NULL, '<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; text-align: left; white-space-collapse: preserve;\"> আলহামদুলিল্লাহ্, দুনিয়াব্যাপী রাসূল সা. এর সীরাত নিয়ে আলোচনা হচ্ছে, লেখালিখি হচ্ছে, প্রবন্ধ হচ্ছে, গ্রন্থ রচিত হচ্ছে। বাংলা সাহিত্যেও সীরাত নিয়ে প্রচুর কাজ হয়েছে। অনেক সম্মানিত শায়েখ ও ওলামায়ে কিরাম সীরাত গ্রন্থ লিখেছেন। পৃথিবীর সেরা সীরাত গ্রন্থগুলোর বাংলা অনুবাদও হয়েছে। বিভিন্ন মুহতারাম শায়েখবৃন্দ রাসূলের জীবনীর ভিন্ন ভিন্ন প্রেক্ষাপটকে সামনে এনে লিখেছেন। প্রত্যেকটি প্রেক্ষাপটে লিখা সীরাত গ্রন্থ উম্মাহর জন্য অতি মূল্যবান হীরকখণ্ড। সীরাত নিয়ে শত কাজের ভিড়ে ‘প্রশ্নোত্তরে সীরাতুন্নবী (সা.)’ গ্রন্থটি একটি নতুন সংযোজন। প্রশ্নোত্তরের মাধ্যমে রাসূল (সা.)-এর জীবনী অধ্যয়নের এই ভিন্ন ধাঁচের গ্রন্থ সীরাতপ্রেমী মানুষদের হৃদয়ে নতুন করে দোলা দিবে ইনশাআল্লাহ। ‘প্রশ্ন’ হলো কৌতূহলের বহিঃপ্রকাশ। আর ‘উত্তর’ হলো উদ্ভূত কৌতূলের নিবৃত্তি। প্রশ্ন এবং তার </span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\"> যে কোন নির্মাণকাজের জন্য নির্মাতাকে পূর্ব প্রস্তুতকৃত কোন মডেল অনুসরণ করতে হয়। মডেল যত নিখুঁত ও সুন্দর হয়, নির্মাণকাজও তত সুন্দর ও অনুপম হয়। বস্তুগত কোন অবকাঠামো নির্মাণের জন্য যেমন মডেল অনুসরণ করতে হয়, ঠিক তেমন একজন মানুষকে মানুষ হিসেবে নির্মাণ করতেও মডেল মানুষের দরকার হয়। সন্দেহাতীতভাবে প্রমাণিত, মানুষ নির্মাণের অনুপম মডেল ‘উসওয়াতুন হাসানা’ বিশ্বনবী মুহাম্মাদ (সা.)। যারা নিজেদের সত্যিকারের মানুষ হিসেবে বিনির্মাণ করতে চান, তাদের অবশ্যই বিশ্ব মানবতার মডেল রাসূলুল্লাহ (সা.)-র অনুসরণ ও অনুকরণ করতে হবে। রাসূল জীবনের পরিপূর্ণ আয়না হচ্ছে সীরাত গ্রন্থ।</span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; text-align: left; white-space-collapse: preserve;\">উত্তর খুঁজে বের করার মাধ্যমে মূলত টেকসই জ্ঞান অর্জিত হয়। যেসব কৌতূহলী পাঠক রাসূল (সা.)-এর জীবন প্রণালী থেকে জ্ঞান অর্জন করতে চান, এ গ্রন্থটি তাঁদের জন্য সহায়ক হিসেবে কাজ করবে বলে আমাদের বিশ্বাস।</span><br></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 04:02:28', '2024-07-04 03:24:27'),
(103, NULL, 22, NULL, NULL, NULL, 237, 29, 0, NULL, 'Nettrytter Prathomic Bojhapora', 'নেতৃত্বের প্রাথমিক বোঝাপড়া', 'Nettrytter-Prathomic-Bojhapora', '31798', NULL, 200, 0, 0, 0, 250, 0, 1, 1, 10, 3, 10, 'upload/products/thumbnails/1803099451878412.jpg', 'upload/products/pdf/1803099451946606.pdf', NULL, '<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Vrinda&quot;,sans-serif;\r\nmso-fareast-font-family:Vrinda\">পারিবারিক, পেশাগত, সামাজিক ও রাজনৈতিক জীবনের জন্য</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%\"><o:p></o:p></span></p>', NULL, '<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"EN\" style=\"font-size:12.0pt;\r\nline-height:115%;font-family:&quot;Vrinda&quot;,sans-serif;mso-fareast-font-family:Vrinda\">ফ্ল্যাপ\r\n: </span></b><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:\r\n&quot;Arial Unicode MS&quot;;mso-fareast-font-family:&quot;Arial Unicode MS&quot;;mso-bidi-font-family:\r\n&quot;Arial Unicode MS&quot;\">আঁধার রাতের মুসাফির অনুসন্ধিুৎসু চোখে শুধুই আলো খুঁজে ফেরে।\r\nকাফেলাকে মঞ্জিলে পৌঁছাতে আলোকমশাল তখন অনিবার্য দিশা। নিকষ আঁধারে দুনিয়া এখন বড্ড\r\nদিশেহারা। মুক্তি কোথায়? টলোমলো জাহাজকে কে তীরে ভেড়াবে? আজ বড়ো প্রয়োজন একঝাঁক দক্ষ\r\nনাবিকের। প্রত্যাশিত নাবিকদের উদ্দেশ্যে ‘নেতৃত্বের প্রাথমিক বোঝাপড়া’।</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%\"><o:p></o:p></span></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 04:13:57', '2024-06-28 04:13:57'),
(104, NULL, 18, NULL, NULL, NULL, 238, 13, 0, NULL, 'Narir porichoy nanan chokhe nanan aynay', 'নারীর পরিচয় : নানান চোখে নানান আয়নায়', 'narir-porichoy-nanan-chokhe-nanan-aynay', '41200', NULL, 120, 0, 0, 0, 170, 20, 2, 1, 10, 5, 10, 'upload/products/thumbnails/1803100213046494.jpg', '', NULL, '<p><span style=\"color: rgb(102, 102, 102); font-family: &quot;hind siliguri&quot;, sans-serif; font-size: 15px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400;\">&nbsp;</span><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\">নারী। অসংখ্য-অগণিত মতবাদ বা ইজমের জটাজালে আটকা পড়া এক নিদারুণ শব্দ। </span><br></p>', NULL, '<p><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\"> বিংশ শতাব্দীর শুরু থেকেই পাশ্চাত্যের আঁস্তাকুড়ে জন্ম নেওয়া নানান চিন্তাধারার বলি হতে হয়েছে খোদ পশ্চিমের নারীকেই। বাদ যায়নি দুনিয়ার অন্য প্রান্তের নারীরাও। পরিণামে নারী ভুলেছে তার আসল অবস্থান, সত্যিকার পরিচয়। নারীর আসল পরিচয় কী? নারী কি পুঁজিবাদী কোম্পানির পণ্যের প্রচারণায় বিলবোর্ডে ঝুলতে থাকা বিজ্ঞাপন? নাকি স্রেফ আকর্ষণীয় কোনো ‘বস্তু’? </span></p><p><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\"> আরব বিশ্বের খ্যাতিমান দাঈ ড. ইয়াদ কুনাইবি এসব প্রশ্নেরই উত্তর খুঁজেছেন। আর তারই ধারাবাহিকতায় একটি অনুসন্ধানী প্রয়াস আপনার হাতের এ বইটি। এতে তিনি নারীকে দেখিয়েছেন নানান চোখে, নারীত্বকে দেখিয়েছেন নানান আয়নায়। সচেতন পাঠকের সামনে পশ্চিমের ভোজবাজির পর্দাটা বেশ খানিকটা দুলিয়ে দেবে এ বই।</span><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\"><br></span><br></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 04:26:03', '2024-07-04 02:57:03'),
(105, NULL, 18, NULL, NULL, NULL, 239, 29, 0, NULL, 'Muktijhuddher Boyane Islam', 'মুক্তিযুদ্ধের বয়ানে ইসলাম', 'Muktijhuddher-Boyane-Islam', '49873', NULL, 140, 0, 0, 0, 175, 0, 1, 1, 10, 3, 10, 'upload/products/thumbnails/1803100241791189.jpg', 'upload/products/pdf/1803100241860808.pdf', NULL, NULL, NULL, '<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"EN\" style=\"font-size:12.0pt;\r\nline-height:115%;font-family:&quot;Vrinda&quot;,sans-serif;mso-fareast-font-family:Vrinda\">ফ্ল্যাপ\r\n: </span></b><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:\r\n&quot;Arial Unicode MS&quot;;mso-fareast-font-family:&quot;Arial Unicode MS&quot;;mso-bidi-font-family:\r\n&quot;Arial Unicode MS&quot;\">মুক্তিযুদ্ধের নির্মাণ ও মুক্তিযুদ্ধকালীন সময়ে ইসলাম যে ডিসকোর্স\r\nহিসেবে হাজির ছিলো, তার অনুপুঙ্খ অনুসন্ধানের একটা প্রয়াস ‘মুক্তিযুদ্ধের বয়ানে ইসলাম’।\r\nইতিহাসের হাত ধরে ঐতিহ্য নির্মিত হয়; ইতিহাসের পাঠ তাই নির্মোই হওয়া সময়ের দাবি। চিন্তাশীল\r\nলেখক পিনাকী ভট্টচার্য মহান মুক্তিযুদ্ধে সংখ্যাগরিষ্ঠের যাপিত ধর্ম আল-ইসলামের অবস্থান\r\nও বয়ানকে উপস্থাপন করতে তথ্য ও প্রমাণের সাগর পাড়ি দিয়েছেন। অবগুণ্ঠন উন্মোচনের এক\r\nইতিহাসের সফরে আপনাকে স্বাগত।</span></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 04:26:30', '2024-06-28 04:26:30'),
(106, NULL, 18, NULL, NULL, NULL, 240, 29, 0, NULL, 'Be Smart With Muhammad (sm,)', 'বি স্মার্ট উইথ মুহাম্মাদ ﷺ', 'Be-Smart-With-Muhammad-(sm,)', '59803', NULL, 156, 0, 0, 0, 195, 0, 1, 1, 10, 3, 10, 'upload/products/thumbnails/1803100826782333.jpg', 'upload/products/pdf/1803100826866667.pdf', NULL, NULL, NULL, '<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"EN\" style=\"font-size:12.0pt;\r\nline-height:115%;font-family:&quot;Vrinda&quot;,sans-serif;mso-fareast-font-family:Vrinda\"><b>ফ্ল্যাপ\r\n:</b> </span></b><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:\r\n&quot;Vrinda&quot;,sans-serif;mso-fareast-font-family:Vrinda\">নবিজির </span><span lang=\"AR-SA\" dir=\"RTL\" style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Times New Roman&quot;,serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;\">ﷺ</span><span dir=\"LTR\"></span><span dir=\"LTR\"></span><span lang=\"AR-SA\" style=\"font-size:12.0pt;line-height:115%;\r\nfont-family:&quot;Vrinda&quot;,sans-serif;mso-fareast-font-family:Vrinda\"><span dir=\"LTR\"></span><span dir=\"LTR\"></span> </span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;\r\nfont-family:&quot;Vrinda&quot;,sans-serif;mso-fareast-font-family:Vrinda\">মতো হওয়া কি খুব\r\nসহজ?</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial Unicode MS&quot;;\r\nmso-fareast-font-family:&quot;Arial Unicode MS&quot;;mso-bidi-font-family:&quot;Arial Unicode MS&quot;\">ঘরে\r\nএসে যখন শুনলেন খাবার নেই, তখন তিনি নফল সিয়ামের নিয়্যত করে ফেললেন। আমরা হলে কী করতাম?</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Vrinda&quot;,sans-serif;\r\nmso-fareast-font-family:Vrinda\">প্রথম কয়েক বছর মানুষের কাছ থেকে বারবার প্রত্যাখ্যাত\r\nহওয়ার পরও কীসের বলে নিরলস দ্বীন প্রচার করে গেছেন? কীভাবে অর্জন করলেন অটুট মানোবল?\r\nকীভাবে রপ্ত করলেন এক অসম্ভব সুন্দর ভাষা, যা শুনলেই মানুষের হৃদয়ে ছাপ ফেলে দিত? কেমন\r\nছিল তাঁর শৈশব, কৈশোর, তারুণ্য আর যৌবনের উচ্ছল দিনগুলো? নবিজির </span><span lang=\"AR-SA\" dir=\"RTL\" style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Times New Roman&quot;,serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;\">ﷺ</span><span dir=\"LTR\"></span><span dir=\"LTR\"></span><span lang=\"AR-SA\" style=\"font-size:12.0pt;line-height:115%;\r\nfont-family:&quot;Vrinda&quot;,sans-serif;mso-fareast-font-family:Vrinda\"><span dir=\"LTR\"></span><span dir=\"LTR\"></span> </span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;\r\nfont-family:&quot;Vrinda&quot;,sans-serif;mso-fareast-font-family:Vrinda\">জীবনী পড়তে গেলে\r\nআমরা সাধারণত তাঁর নবুওয়্যত পরবর্তী জীবনেই বেশি গুরুত্ব দিই, কিন্তু এর ভিত্তিটা যে\r\nমহান আল্লাহ তাঁর নবুওয়্যতপূর্ব ৪০ বছরের জীবনে ধীরে ধীরে তৈরি করেছিলেন, সেটা কজন\r\nঘেঁটে দেখি?</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial Unicode MS&quot;;\r\nmso-fareast-font-family:&quot;Arial Unicode MS&quot;;mso-bidi-font-family:&quot;Arial Unicode MS&quot;\">প্রচলিত\r\nঅর্থে কোনো সিরাত বই নয় এটি। কোনো তাত্ত্বিক ঘটনার বিবরণও না। এখানে আপনি পাবেন ব্যবহারিক\r\nকিছু জ্ঞান। হাতে-কলমে শিখবেন নিজের বাচ্চাকে নবিজির </span><span lang=\"AR-SA\" dir=\"RTL\" style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Times New Roman&quot;,serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;\">ﷺ</span><span dir=\"LTR\"></span><span dir=\"LTR\"></span><span lang=\"AR-SA\" style=\"font-size:12.0pt;line-height:115%;\r\nfont-family:&quot;Arial Unicode MS&quot;;mso-fareast-font-family:&quot;Arial Unicode MS&quot;;\r\nmso-bidi-font-family:&quot;Arial Unicode MS&quot;\"><span dir=\"LTR\"></span><span dir=\"LTR\"></span>\r\n</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial Unicode MS&quot;;\r\nmso-fareast-font-family:&quot;Arial Unicode MS&quot;;mso-bidi-font-family:&quot;Arial Unicode MS&quot;\">মতো\r\nকরে বড়ো করার উপায়। টিনএজ বয়সি হলে জানতে পারবেন এই উড়ুউড়ু সময়টাতে নিজেকে বাগে রাখার\r\nকৌশল। বিবাহিত হলে আছে দুজনে মিলে জীবনটাকে আরও মধুর করার টোটকা। সর্বোপরি নবিজির\r\n</span><span lang=\"AR-SA\" dir=\"RTL\" style=\"font-size:12.0pt;line-height:115%;\r\nfont-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:&quot;Times New Roman&quot;\">ﷺ</span><span dir=\"LTR\"></span><span dir=\"LTR\"></span><span lang=\"AR-SA\" style=\"font-size:12.0pt;\r\nline-height:115%;font-family:&quot;Arial Unicode MS&quot;;mso-fareast-font-family:&quot;Arial Unicode MS&quot;;\r\nmso-bidi-font-family:&quot;Arial Unicode MS&quot;\"><span dir=\"LTR\"></span><span dir=\"LTR\"></span>\r\n</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial Unicode MS&quot;;\r\nmso-fareast-font-family:&quot;Arial Unicode MS&quot;;mso-bidi-font-family:&quot;Arial Unicode MS&quot;\">মতো\r\nজীবনের প্রতিটি ক্ষেত্রে ‘স্মার্ট’ হওয়ার তরিকা। তবে চলুন স্মার্ট হই প্রিয় নবিজির\r\n</span><span lang=\"AR-SA\" dir=\"RTL\" style=\"font-size:12.0pt;line-height:115%;\r\nfont-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:&quot;Times New Roman&quot;\">ﷺ</span><span dir=\"LTR\"></span><span dir=\"LTR\"></span><span lang=\"AR-SA\" style=\"font-size:12.0pt;\r\nline-height:115%;font-family:&quot;Vrinda&quot;,sans-serif;mso-fareast-font-family:Vrinda\"><span dir=\"LTR\"></span><span dir=\"LTR\"></span> </span><span lang=\"EN\" style=\"font-size:\r\n12.0pt;line-height:115%;font-family:&quot;Vrinda&quot;,sans-serif;mso-fareast-font-family:\r\nVrinda\">মতো</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;\r\nfont-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:&quot;Times New Roman&quot;\">…<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%\">&nbsp;</span></p><p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%\">&nbsp;</span></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 04:35:48', '2024-06-28 04:35:48'),
(107, NULL, 18, NULL, NULL, NULL, 241, 29, 0, NULL, 'Biye', 'বিয়ে', 'Biye', '95091', NULL, 152, 0, 0, 0, 190, 0, 1, 1, 10, 3, 10, 'upload/products/thumbnails/1803101113095122.jpg', 'upload/products/pdf/1803101113147497.pdf', NULL, NULL, NULL, '<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"EN\" style=\"font-size:12.0pt;\r\nline-height:115%;font-family:&quot;Vrinda&quot;,sans-serif;mso-fareast-font-family:Vrinda\"><b style=\"background-color: rgb(255, 255, 255);\">ফ্ল্যাপ\r\n:</b> </span></b><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:\r\n&quot;Arial Unicode MS&quot;;mso-fareast-font-family:&quot;Arial Unicode MS&quot;;mso-bidi-font-family:\r\n&quot;Arial Unicode MS&quot;\">একটি ধর্মীয় চুক্তি, একটি সামাজিক সম্পর্ক। একে ঘিরে দুজন মানুষ,\r\nদুটি পরিবারে কত আকাক্সক্ষা, কত স্বপ্ন! সম্পর্কটিকে সুন্দর করার জন্য প্রয়োজন সকলের\r\nসম্মিলিত প্রচেষ্টা। তাই এই বইটি কেবল বিবাহেচ্ছু, নববিবাহিত বা বিবাহিতাদের জন্য নয়;\r\nবরং তাদের বাবা-মা, শ্বশুর-শাশুড়ি, আত্মীয়স্বজনদের জন্যও। এখানে বিয়ের স্বরূপ, দায়িত্বসমূহ,\r\nকরণীয় ও বর্জনীয় বিষয়াদি নিয়ে কিছু আলোচনা রয়েছে; যা হয়তো চিন্তার দিগন্তে নতুনত্ব\r\nআনতে পারে। রয়েছে কিছু গল্প; যা জীবনের বাস্তবতার প্রতিফলন।</span></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 04:40:21', '2024-06-28 04:40:21'),
(108, NULL, 18, NULL, NULL, NULL, 242, 29, 0, NULL, 'The Reverse: Fire Asar Golpo', 'দ্যা রিভার্টস : ফিরে আসার গল্প', 'The-Reverse:-Fire-Asar-Golpo', '71672', NULL, 208, 0, 0, 0, 260, 0, 1, 1, 10, 3, 10, 'upload/products/thumbnails/1803102490053630.jpg', 'upload/products/pdf/1803102490127090.pdf', NULL, NULL, NULL, '<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; text-align: left; white-space-collapse: preserve;\"> পৃথিবীর ১৩ জন বিখ্যাত মানুষ। তাঁরা কেউবা ছিলেন খ্রিস্টান কেউ ইহুদী আর কেউবা হিন্দু। তাঁরা ফিরে এসেছেন চিরশান্তির পথ ইসলামে। তাঁদের এই জার্নিটা মোটেও সহজ ছিল না। তাঁদের ইসলামে ফিরে আসার এই জার্নি নিয়ে প্রকাশিত হয়েছে ‘দ্যা রিভার্টস: ফিরে আসার গল্প’ বইটি। </span><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\"> আফগান যুদ্ধ কাভার করতে এসে তালেবানদের হাতে ধরা পড়লেন এক নারী সাংবাদিক। </span><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\"> অন্যদিকে, ফিলিস্তিনের গাজায় এসে আটকে গেলেন এক ব্রিটিশ তরুণী। তারপর কী হলো তাদের? </span><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\"> একজন খ্রিস্টান পাদ্রী, একজন ধার্মিক বৌদ্ধ, অনুশাসন মানা একজন হিন্দু যুবক আর মামার আমন্ত্রনে ফিলিস্তিনে ঘুরতে আসা পোলান্ডের এক ইহুদী তরুণ। চার ধর্মের চারজন। কেমন করে পাল্টে গেলেন সবাই? </span><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\"> বাবরি মসজিদ নিজ হাতে ভেঙেছেন বলবির সিং। এক সময়ে যা নিয়ে অনেক গর্ববোধ করতেন। কিন্তু তার মনে কীসের এত ব্যথা আজ? বাবরি মসজিদ ভেঙে দেওয়া হাত আজ কেন মসজিদ গড়ার কাজে ব্যস্ত? </span><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\"> বাবরি মসজিদ নিজ হাতে ভেঙেছেন বলবির সিং। এক সময়ে যা নিয়ে অনেক গর্ববোধ করতেন। কিন্তু তার মনে কীসের এত ব্যথা আজ? বাবরি মসজিদ ভেঙে দেওয়া হাত আজ কেন মসজিদ গড়ার কাজে ব্যস্ত? </span><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\"> আধুনিক আমেরিকার দুজন মানুষ। একজন অবিশ্বাসী নাস্তিক। অন্যজন সমাজতান্ত্রিক বিপ্লবের স্বপ্নে বিভোর। দুজনের জীবনে নাটকীয় পরিবর্তন এল। কিন্তু কী করে?</span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\">MTV চ্যানেলের বিশ্ববিখ্যাত এক উপস্থাপিক। পুরো ইউরোপের ঘরে ঘরে পরিচিত মুখ। একদিন দেখা হলো, পাকিস্তানের বিশ্বকাপ জয়ী ক্রিকেটার ইমরান খানের সাথে। তারপর?</span></p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><font color=\"#1f1f1f\" face=\"monospace\"><span style=\"font-size: 12px; white-space-collapse: preserve;\">জানতে হলে পড়তে হবে বইটি আপনাকে......</span></font></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 05:02:14', '2024-07-04 03:28:55'),
(109, NULL, 18, NULL, NULL, NULL, 244, 29, 0, NULL, 'Bandhan', 'বন্ধন', 'Bandhan', '21746', NULL, 156, 0, 0, 0, 195, 0, 1, 1, 10, 3, 10, 'upload/products/thumbnails/1803102743551778.jpg', 'upload/products/pdf/1803102743616277.pdf', NULL, NULL, NULL, '<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span style=\"color: rgb(31, 31, 31); font-family: monospace; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; text-align: left; white-space-collapse: preserve;\"> ইসলামের আত্মিক শান্তি ও সামাজিক শীতলতা যদি এই পৃথিবীতে প্রতিষ্ঠিত করতে চাই, তবে শুরুতেই ইসলামকে আমাদের পরিবারে প্রতিষ্ঠা করতে হবে। ইসলামের বাণী পৃথিবীর সর্বত্র ছড়িয়ে পড়বে তখনি যখন প্রত্যেক মুসলিম সন্তান বড় হবে ইসলামকে তার হৃদয়ে ধারণ করে। পৃথিবীতে ন্যায় প্রতিষ্ঠা হবে তখনই যখন পরিবারের সদস্যরা অন্যায় থেকে বিরত থাকবে। বাবা-মা সন্তানের হক নষ্ট করবে না, আবার সন্তান বাবা-মা এর প্রতি নীতিপরায়ণ হবে। স্বামী-স্ত্রী একে অপরের দুর্বলতার সুযােগ নিবে না বরং একজন আরেকজনের ঢাল স্বরুপ হবে। কিন্তু বর্তমানে মুসলিম সমাজে ইসলামের দেখিয়ে দেওয়া পথ অনুসরণ তাে হচ্ছেই না বরং কুরআনের আয়াত অপব্যবহার করে স্বামী স্ত্রীর অধিকার ছিনিয়ে নিচ্ছেন, নিজ কর্তব্য পালন না করে অধিকারের কথা বলছেন, অভিভাবক সন্তানের উপর তাদের অন্যায্য সিদ্ধান্ত চাপিয়ে দিচ্ছেন। মুসলিম পরিবারকে এসব অনাচার থেকে রক্ষা করার তাগিদ থেকেই উস্তাদ নুমান আলী খান পরিবারে আমাদের দায়িত্ব ও কর্তব্য নিয়ে কুরআনের ভিন্ন ভিন্ন আয়াতের আলােকে বিভিন্ন সময়ে কিছু বক্তব্য দিয়েছেন; যার সংকলিত সূতিকাগার হচ্ছে এই ‘বন্ধন’ বইটি। </span><br></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 05:06:16', '2024-07-04 02:59:14'),
(110, NULL, 25, NULL, NULL, NULL, 243, 30, 0, NULL, 'Lipstick', 'লিপস্টিক', 'Lipstick', '83544', NULL, 304, 0, 0, 0, 460, 20, 2, 1, 10, 5, 9, 'upload/products/thumbnails/1803103062093172.jpg', 'upload/products/pdf/1803103062180378.pdf', NULL, NULL, NULL, '<p><span style=\"font-family: Arial; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\">মানুষ তার স্বপ্নে বাঁচে। নিমার সাদামাটা জীবনকে লিপস্টিকের রঙিন পরশ এঁকে দেয় এক নতুন পরিচয়। বহু দিনের স্বপ্নকে বাস্তব রূপে দেখার পরিচয়। এই কঠিন পথ পাড়ি দেয়া নিমার জন্য সহজ ছিল না। জাদিবের মতন একজন তার চলার পথে সাহস না জোগালে সে হয়তো হারিয়ে যেত হতাশার অমানিশায়। জাদিব গল্পের অন্যতম প্রধান চরিত্র, যে সীমাহীন অনিশ্চয়তার মাঝেও আশায় বুক বাঁধার মতন শক্তিশালী ব্যক্তিত্বের অধিকারী।\r\nমানুষের ভাবনা কিংবা দুর্ভাবনা অনেক সময় তন্দ্রার আবেশে তার সামনে দৃশায়িত হয়। লিপস্টিককে কেন্দ্র করে তেমনই এক দুর্ভাবনা বারবার নিমার মাঝে আসে, সেটাকে উপন্যাসের একটা ইন্টারেস্টিং পার্ট বলা যেতে পারে। আদৌ কি সে ব্যাপারটার সাথে তার জীবনের সংযোগ ছিল সেই প্রশ্নের উত্তর নাহয় তোলা থাক।\r\n‘লিপস্টিক’ জাদিব, নিমার ভালোবাসার গল্প। শত বাঁধা পেরিয়ে ছুটে চলা নারীর জিতে যাওয়ার গল্প। সুরের ধারায় শব্দগুচ্ছকে গাঁথতে জানে জাদিব নামের যে মানুষটা, শেষ পর্যন্ত কি সে নিমাকে বাঁধতে পারবে হৃদয় গহিনে?</span><br></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 05:11:20', '2024-07-06 03:34:14'),
(111, NULL, 22, NULL, NULL, NULL, 245, 29, 0, NULL, 'Erdogan : The Change Maker', 'এরদোয়ান : দ্যা চেঞ্জ মেকার', 'Erdogan-The-Change-Maker', '84670', NULL, 252, 0, 0, 0, 315, 0, 1, 1, 10, 3, 10, 'upload/products/thumbnails/1803103066896942.jpg', 'upload/products/pdf/1803103066940308.pdf', NULL, NULL, NULL, '<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"EN\" style=\"font-size:12.0pt;\r\nline-height:115%;font-family:&quot;Vrinda&quot;,sans-serif;mso-fareast-font-family:Vrinda\">ফ্ল্যাপ\r\n: </span></b><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:\r\n&quot;Arial Unicode MS&quot;;mso-fareast-font-family:&quot;Arial Unicode MS&quot;;mso-bidi-font-family:\r\n&quot;Arial Unicode MS&quot;\">রেজেপ তায়্যিপ এরদোয়ান। বর্তমান বিশ্বের অন্যতম একজন ক্যারিশম্যাটিক\r\nও জনপ্রিয় নেতা। বিংশ শতাব্দীর শেষ ও একবিংশ শতাব্দীর শুরুর দিকে এই নেতার উত্থান।\r\n২০০১ সালে একে পার্টি গঠনের মাধ্যমে ‘নতুন তুরস্ক’ গড়ার স্বপ্ন নিয়ে ২০০২ সালের নির্বাচনে\r\nজনগণের ব্যাপক সমর্থন নিয়ে প্রথমবারের মতো ক্ষমতায় আসেন তিনি। টানা ১৫ বছর ধরে ক্ষমতায়।\r\nতাঁর নেতৃত্বকেই তুরস্ক বিশে^র বুকে মাথা উঁচু করে দাঁড়িয়েছে। অর্থনীতি, শিক্ষা, স্বাস্থ্য,\r\nযোগাযোগ ও অবকাঠামোসহ সকল খাতে ব্যাপক উন্নয়নের ছোঁয়া লেগেছে। বর্তমান তুরস্ক উন্নয়নের\r\nরোল মডেল হিসেবে বিশ্বের কাছে প্রাধান্য পাচ্ছে। শুধু উন্নয়নই নয়; এরদোয়ান জয় করেছেন\r\nতাঁর দেশের সাধারণ জনগণের হৃদয়ও। তুরস্কের সীমানা ছাড়িয়ে দুনিয়াব্যাপী তাঁর জনপ্রিয়তা\r\nউত্তরোত্তর বেড়েই চলেছে। বিশ্ব রাজনীতিতে এরদোয়ান একজন অন্যতম নীতিনির্ধারকে পরিণত\r\nহয়েছেন। বিশ্বের মজলুম মানুষের পাশে তিনি সবচেয়ে বিশ্বস্ত বন্ধু হিসেবে দাঁড়িয়েছেন।\r\nতবে এরদোয়ানের এই দীর্ঘ সফলতার পথচলা মোটেই সহজ ছিল না। অনেক কঠিন পথ পাড়ি দিয়ে তাঁকে\r\nএখানে এসে পৌঁছতে হয়েছে। গ্রহণ করতে হয়েছে বিশাল চ্যালেঞ্জ, মোকাবিলা করতে হয়েছে বড়ো\r\nবড়ো বাধা ও পরীক্ষা। কিন্তু</span><span lang=\"EN\" style=\"font-size:12.0pt;\r\nline-height:115%;font-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:\r\n&quot;Times New Roman&quot;\"> </span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:\r\n115%;font-family:&quot;Arial Unicode MS&quot;;mso-fareast-font-family:&quot;Arial Unicode MS&quot;;\r\nmso-bidi-font-family:&quot;Arial Unicode MS&quot;\">&nbsp;তিনি থেমে যাননি; শুধু সামনেই এগিয়েছেন। করেছেন বড়ো\r\nবড়ো পরিবর্তন। আর তাই তো তাঁর সবচেয়ে বড়ো পরিচয়, তিনি ‘চেঞ্জ মেকার’।</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%\"><o:p></o:p></span></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 05:11:24', '2024-06-28 05:11:24'),
(112, NULL, 22, NULL, NULL, NULL, 246, 29, 0, NULL, 'Rohingya Gonohotta Kathgoray Su chi', 'রোহিঙ্গা গণহত্যা : কাঠগড়ায় সু চি', 'Rohingya-Gonohotta-Kathgoray-Su-chi', '99585', NULL, 120, 0, 0, 0, 150, 0, 1, 1, 10, 3, 10, 'upload/products/thumbnails/1803103697827226.jpg', 'upload/products/pdf/1803103697872197.pdf', NULL, NULL, NULL, '<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"EN\" style=\"font-size:12.0pt;\r\nline-height:115%;font-family:&quot;Vrinda&quot;,sans-serif;mso-fareast-font-family:Vrinda\">ফ্ল্যাপ\r\n: </span></b><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:\r\n&quot;Arial Unicode MS&quot;;mso-fareast-font-family:&quot;Arial Unicode MS&quot;;mso-bidi-font-family:\r\n&quot;Arial Unicode MS&quot;\">জাতিসংঘের ভাষ্যমতে, বর্তমান বিশ্বে সবচেয়ে নির্যাতিত জনগোষ্ঠীর\r\nনাম হলো, ‘রোহিঙ্গা’। মিয়ানমার নামক রাষ্ট্রীয় দানবের গণহত্যা ও নির্মূল অভিযানে রোহিঙ্গারা\r\nজন্মভূমি আরাকান থেকে বিতাড়িত হলেও পরিতাপের বিষয় হচ্ছে, গোটাবিশ্ব তাদের রক্ষার্থে\r\nজোরালো কোনো পদক্ষেপ নিচ্ছে না। রোহিঙ্গা ইস্যুতে ক্ষমতাধর রাষ্ট্রগুলোর দ্বান্দ্বিক\r\nঅবস্থানের পেছনে জাতীয় স্বার্থ, ভূ-রাজনীতি এবং অর্থনৈতিক স্বার্থের যেসব ইস্যু রয়েছে,\r\nবস্তুনিষ্ঠভাবে দেখানোর চেষ্টা করেছেন। শুধু ধর্মীয় কারণে এবং সংখ্যাগরিষ্ঠ বর্মি ও\r\nরাখাইন মগদের সমর্থন নিজের পক্ষে রাখার আশায় নোবেল মগদের সমর্থন নিজের পক্ষে রাখার\r\nআশায় নোবেল বিজয়ী অং সান সু চি নীতি-নৈতিকতা বিসর্জন দিয়ে কীভাবে আজ রোহিঙ্গাবিরোধী\r\nসাম্প্রদায়িক শক্তি হিসেবে আবির্ভূত হয়েছেন, তার স্পষ্ট ধারণা পাওয়া যাবে এ বই থেকে।\r\nগুরুত্বপূর্ণ পয়েন্ট ও তথ্যের বিষয়ে লেখকের ফুটনোট ও রেফারেন্স ব্যবহার বইটিকে দালিলিক\r\nমর্যাদা দেওয়ার পাশাপাশি পাঠকের কাছে বিশ্বাসযোগ্য করে তুলবে। সু চি ও সেনাপ্রধান জেনারেল\r\nহ্লাইং অসহায় রোহিঙ্গা জনগোষ্ঠীর ওপর কী নিষ্ঠুর ও নৃশংস অত্যাচার চালিয়েছে, শত বছর\r\nপরেও বইটি তার একটি দলির ও প্রমাণপত্র হয়ে থাকবে।</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%\"><o:p></o:p></span></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 05:21:26', '2024-06-28 05:21:26'),
(113, NULL, 25, NULL, NULL, NULL, 247, 30, 0, NULL, 'Opekkha', 'অপেক্ষা', 'Opekkha', '97085', NULL, 218, 0, 0, 0, 335, 20, 2, 1, 10, 5, 10, 'upload/products/thumbnails/1803103935201796.jpeg', 'upload/products/pdf/1803103935336511.pdf', NULL, NULL, NULL, '<p><span style=\"font-family: Arial; font-size: 13px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\">নির্জন প্রান্তরে পানির উপরে ভেসে থাকা একটি গাছের গুঁড়িতে একাকী বসে আছে তমা। তমা অনুভব করছে, সে যেন কিছু একটা চায়। কিছু একটার জন্য সে অপেক্ষায় আছে। কিন্তু সেই কিছুটা কি? তমা তা কিছুতেই বুঝতে পারছে না। সে খুঁজে চলেছে তার এই অর্থহীন জীবনের মানে? সাদা চাদরে ঢাকা তার এই জীবন বড্ড বেশি বর্ণহীন। তার জীবনে কোনও ছন্দ নেই, চাওয়া নেই, কোনও লক্ষ্য নেই। নেই কোনও লাভ লোকসানের হিসাবের খাতা। সবই যেন শূন্য। সে ভাবছে কিছুই যদি নেই, তাহলে এই জীবনটাই বা কেন? তার জীবন কি এখানেই শেষ হওয়া প্রয়োজন? \r\nঠিক এই সময়ে একটি পুরুষ কণ্ঠ বলে উঠল, কি হলো তোমার? সুইসাইড করবে নাকি? \r\nতমা জানে না কে এই পুরুষ। সে কি মানুষ নাকি কোনও অশরীরী। ক্রমশই এই একটি কণ্ঠস্বর জাদুর মতো বদলে দিতে লাগল তার জীবন।</span><br></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 05:25:12', '2024-07-03 07:45:07'),
(114, NULL, 18, NULL, NULL, NULL, 248, 29, 0, NULL, 'Akash Choa Shopno', 'আকাশ ছোঁয়া স্বপ্ন', 'Akash-Choa-Shopno', '48122', NULL, 136, 0, 0, 0, 170, 0, 1, 1, 10, 3, 10, 'upload/products/thumbnails/1803104332666569.jpg', 'upload/products/pdf/1803104332708525.pdf', NULL, NULL, NULL, '<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"EN\" style=\"font-size:12.0pt;\r\nline-height:115%;font-family:&quot;Vrinda&quot;,sans-serif;mso-fareast-font-family:Vrinda\">ফ্ল্যাপ\r\n: </span></b><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:\r\n&quot;Arial Unicode MS&quot;;mso-fareast-font-family:&quot;Arial Unicode MS&quot;;mso-bidi-font-family:\r\n&quot;Arial Unicode MS&quot;\">একজন মানুষ তার স্বপ্নের সমান বড়ো। পৃথিবীর সব মানুষই কমবেশি স্বপ্ন\r\nদেখে। ঘুমের ঘোরে হঠাৎ ঘোরা হাঁকিয়ে ছুটে যাওয়া, অথই সাগরে ডুবতে ডুবতে হারিয়ে যাওয়া,\r\nকুঁড়ে ঘরে শুয়ে রাজপ্রাসাদের ঘুরে বেড়ানো, দূর নীলিমায় পাখির ন্যায় আকাশ ছোঁয়া- মেন\r\nহাজারো স্বপ্ন আমাদের নিত্য সঙ্গী। আমি একদিন স্বপ্ন দেখলাম, আগ্রার তজমহল ঘুরে ঘুরে\r\nদেখছি। আর একদিন দেখলাম, বাইতুল্লাহ শরিফ তাওয়াফ করছি। এরই মধ্যে হঠাৎ করে যেন কারও\r\nস্পর্শে ঘুম ভেঙে গেল। জেগে উঠে দেখি, শিয়রে মা দাঁড়িয়ে আছেন। এমনটা আমাদের প্রায়ই\r\nহয়। এসব স্বপ্ন ঘুমিয়ে দেখা স্বপ্ন। কিন্তু আমি তোমাদের যে স্বপ্নের কথা বলছি, তা জেগে\r\nদেখার স্বপ্ন এবং স্বপ্ন জেগেই দেখতে হয়।</span><span lang=\"EN\" style=\"font-size:\r\n12.0pt;line-height:115%\"><o:p></o:p></span></p><p>\r\n\r\n</p><p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Vrinda&quot;,sans-serif;\r\nmso-fareast-font-family:Vrinda\">এপিজে আবুল কালাম-এর ভাষায়</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Times New Roman&quot;,serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;\">-</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial Unicode MS&quot;;\r\nmso-fareast-font-family:&quot;Arial Unicode MS&quot;;mso-bidi-font-family:&quot;Arial Unicode MS&quot;\">‘ঘুমের\r\nমধ্যে দেখা স্বপ্ন আসল স্বপ্ন নয়, স্বপ্ন সেটা, যা মানুষকে ঘুমাতে দেয় না।’</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%\"><o:p></o:p></span></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 05:31:31', '2024-06-28 05:31:32'),
(115, NULL, 26, NULL, NULL, NULL, 249, 29, 0, NULL, 'Sanjak E Usman', 'সানজাক-ই উসমান', 'Sanjak-E-Usman', '31663', NULL, 384, 0, 0, 0, 480, 0, 1, 1, 10, 3, 10, 'upload/products/thumbnails/1803104789105996.jpg', 'upload/products/pdf/1803104789158587.pdf', NULL, NULL, NULL, '<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><b><span lang=\"EN\" style=\"font-size:12.0pt;\r\nline-height:115%;font-family:&quot;Vrinda&quot;,sans-serif;mso-fareast-font-family:Vrinda\">ফ্ল্যাপ\r\n: </span></b><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:\r\n&quot;Vrinda&quot;,sans-serif;mso-fareast-font-family:Vrinda\">আপনি কয়জন সিরিয়াল কিলারকে চেনেন?\r\nএই জগতের ইতিহাসে ভয়ংকরতম খুনির সাথে কি আপনার দেখা হয়েছে?</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%\"><o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial Unicode MS&quot;;\r\nmso-fareast-font-family:&quot;Arial Unicode MS&quot;;mso-bidi-font-family:&quot;Arial Unicode MS&quot;\">‘সানজাক-ই\r\nউসমান আপনাকে তার সাথে দেখা করিয়ে দিতে চলেছে। তাকে দেখতে হলে আমাদের উঁকি দিতে হবে\r\nআটশো বছর আগের পৃথিবীতে।</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:\r\n115%\"><o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial Unicode MS&quot;;\r\nmso-fareast-font-family:&quot;Arial Unicode MS&quot;;mso-bidi-font-family:&quot;Arial Unicode MS&quot;\">ত্রয়োদশ\r\nশতাব্দীর শুরুতেই মঙ্গোলিয়ান স্তেপ থেকে যেন স্বয়ং আজরাইল হয়ে নেমে এলেন চেঙ্গিজ খান\r\nএবং তার মোঙ্গল বাহিনী। মাত্র কুড়ি বছরের ভেতর যেন নরকে পরিণত হলো সারা পৃথিবী। প্রথমে\r\nচীন, তারপর তুর্কিস্তান আর খোরাসান হয়ে মোঙ্গলঝড় ধেয়ে এলো ককেশাস, আনাতোলিয়া দিয়ে রাশিয়া\r\nআর হিন্দুস্তানের দিকে। মরে সাফ হয়ে গেল কোটি কোটি মানুষ।</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%\"><o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial Unicode MS&quot;;\r\nmso-fareast-font-family:&quot;Arial Unicode MS&quot;;mso-bidi-font-family:&quot;Arial Unicode MS&quot;\">মোঙ্গলদের\r\nহাত থেকে কোনোমতে প্রাণ বাঁচিয়ে ইরান তুর্কিস্তান থেকে আনাতোলিয়ার দিকে রওনা দিলো কিছু\r\nমানুষ।</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%\"><o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Vrinda&quot;,sans-serif;\r\nmso-fareast-font-family:Vrinda\">তারপর কী হলো? কী করে তারা গড়ে তুলল বিশাল এক সালতানাত?\r\nমোঙ্গলদের হাত থেকে কারা বাঁচালো মক্কা-মদিনাকে?</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%\"><o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%;font-family:&quot;Arial Unicode MS&quot;;\r\nmso-fareast-font-family:&quot;Arial Unicode MS&quot;;mso-bidi-font-family:&quot;Arial Unicode MS&quot;\">এটা\r\nকোনো নিয়মিত ইতিহাসের বই বা কোনো ঐতিহাসিক উপন্যাস নয়। এটা একই সঙ্গে ইতিহাস, ফিকশন\r\nআর থ্রিলার। আজকের পৃথিবী কী করে নির্মাণ হলো, তা জানতে এই বই আপনাকে দারুণ সহযোগিতা\r\nকরবে ইনশাআল্লাহ।</span><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%\"><o:p></o:p></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-top:6.0pt;text-align:justify\"><span lang=\"EN\" style=\"font-size:12.0pt;line-height:115%\">&nbsp;<o:p></o:p></span></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 05:38:47', '2024-06-28 05:38:47'),
(116, NULL, 25, NULL, NULL, NULL, 250, 30, 0, NULL, 'Joltrishna', 'জলতৃষ্ণা', 'joltrishna', '53856', NULL, 325, 0, 0, 0, 500, 20, 2, 1, 10, 5, 10, 'upload/products/thumbnails/1803104928297450.jpg', 'upload/products/pdf/1803104928690495.pdf', NULL, NULL, NULL, '<p><span style=\"font-family: Arial; font-size: 13px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; white-space-collapse: preserve;\">গ্রাম শহরের কাছে না গেলেও শহর এখন ঢুকে পড়ে গ্রামের মধ্যে। মুক্তবাজার পুঁজিবাদী রাষ্ট্র মানেই ক্ষমতার সাথে সংশ্লিষ্ট ব্যক্তিদের অবাধ লুটের লাইসেন্স দেওয়া। প্রাথমিক পুঁজি সংগ্রহের জন্য অবাধ লুণ্ঠন পুঁজিবাদ নির্মাণের শর্ত হিসেবে গ্রহণ করা হয়। ইউরোপ এই পুঁজি সংগ্রহ করেছিল এশিয়া-আফ্রিকা-লাতিন আমেরিকার দেশগুলোকে লুণ্ঠনের মাধ্যমে। বাংলাদেশের সেই সক্ষমতা নেই। তাই তারা লুঠ করে দেশেরই সম্পদ, জঙ্গল, নদী, পাহাড়, খনি, আর শ্রমজীবী মানুষের শ্রম। সেই লুণ্ঠনের বিরুদ্ধে সংগঠিত আন্দোলন আমাদের দেশে অনুপস্থিত। তবে স্থানীয়ভাবে কিছু দেশপ্রেমিক মেরুদণ্ডসম্পন্ন তরুণ রুখে দাঁড়ায়। অবধারিতভাবেই তারা পরাজিত হয় অসম যুদ্ধে। কিন্তু আমাদের জাতির হাজার বছরের ইতিহাস বলে যে পরাজয় শেষ কথা নয়। প্রতিবাদ এবং বারবার রুখে দাঁড়ানোই সত্য এবং গুরুত্বপূর্ণ। \r\nশাহমুব জুয়েলের ‘জলতৃষ্ণা’ আপাতদৃষ্টিতে পরাজয়ের পাশাপাশি সেই অবিনাশী প্রতিরোধের সংকল্পটিকেই সামনে এনেছে। উপন্যাসটি এই কারণেই আমার কাছে গুরুত্বপূর্ণ একটি প্রচেষ্টা হিসেবে পরিগণিত। লেখকের ভাষা এবং আখ্যান-বয়নে পরিশ্রমের ছাপ রয়েছে। সবমিলিয়ে বলা যায়, শাহমুব জুয়েলের উপন্যাস ‘জলতৃষ্ণা’ নতুন প্রজন্ম লেখকদের প্রতি আশাবাদী চিন্তার ফসল।\r\nজাকির তালুকদার\r\nকথাসাহিত্যিক</span><br></p>', '[]', 0, '[]', NULL, 1, 0, 1, 1, 0, 0, 0, 1, 4, '2024-06-28 05:41:00', '2024-07-03 07:46:07');
