(35, 177, 'ইফতেখার আবির', '--tgJwu', 'no address', 'আমি রোদরঞ্জন হতে চেয়েছিলাম।\r\nসামান্য উত্তাপে মরে যেতাম বলে তুমি আগলে রাখতে আমায়\r\nএতটুকু পানির আধিক্যে আমি কান্নায় ভেঙে পড়তাম।\r\nতুমি জড়িয়ে ধরে চোখের জল মুছতে।\r\nতোমার ভালোবাসার মাত্রাতিরিক্ত অধিকারের নেশায় আমি\r\nরোদরঞ্জন হয়ে ফুটতে চেয়েছিলাম।\r\nআফসোস, বিধাতা আমায় বানালেন কাঁটাওয়ালা ক্যাকটাস।', NULL, 1, 4, NULL, NULL),
(36, 178, 'জামিল হাদী', '--1URIP', 'no address', 'যদি প্রথমেই ‘আমি কে’ এটা জানার জন্য সব লেখা বাদ রেখে ব্যাক ফ্ল্যাপ পড়তে শুরু করেন, তাহলে এটুকু বলব আমি আপনার সেই পুরোনো প্রতিবেশী, যার সাথে আপনার দেখাটা হয়েও হলো না। ইচ্ছাকৃতভাবেই অপরিচিত থেকে গেলাম আমরা। আর যদি বইটি পড়া শেষ হয়ে থাকে তাহলে এতক্ষণে আপনি জেনে গেছেন ‘আমি কে’। আমরা মন যমজ। আমরা একজন আরেকজনের পরমাত্মীয়।', NULL, 1, 4, NULL, NULL),
(37, 179, 'স্বপ্নীল চক্রবর্ত্তী', '--O9q3D', 'no address', 'গল্প, উপন্যাস বা স্মৃতিকথা থেকে কবিতাকে খুব সহজেই আলাদা করা যায়। এইযে অস্থির দিনগুলো চলে যাচ্ছে টের পাওয়ার আগেই, স্মৃতিগুলো হাওয়াই মিঠাইয়ের মতো, ধরার আগেই নেই৷ বিরহে আছি নাকি প্রেমে আছি বুঝে উঠতে না পারা-\r\nসেইসব ভাঙাচোরা দৃশ্য থেকেই কিছু কথা কবিতার মতো উঠে এসেছে এই বইয়ে৷\r\nকোলাহল, চেঁচামেচি, উৎসব, আলিঙ্গন অথবা প্রত্যাখ্যান;\r\nসবকিছুকে একপাশে সরিয়ে রেখে কোনো ব্যক্তিগত ফাঁকা সময়ে বইটা নিয়ে বসতে পারেন। আমার বিষণ্ণ রোলারকোস্টারে আপনাকে স্বাগত জানাই।\r\n~স্বপ্নীল চক্রবর্ত্তী', NULL, 1, 4, NULL, NULL),
(38, 180, 'আখতার বানু শেফালি', '---SbKxW', 'টাংগাইল জেলার মংগল হোড় গ্রাম।', 'আখতার বানু শেফালি টাংগাইল জেলার মংগল হোড় গ্রামে ১৯৫১ সালের ৮ই জুলাই জন্মগ্রহণ করেন। তিনি টাংগাইলের বিন্দু বাসিনী গার্লস স্কুল ও কুমুদিনী কলেজে পড়াশোনা করেন। কবি কিশোরী বয়সে ইত্তেফাকের কচি কাঁচার আসর, আজাদের মুকুলের মহফিল, তৎকালীন পশ্চিম পাকিস্তান থেকে প্রকাশিত পাকিস্তানি খবরের কিশোর প্রাঙ্গণে নিয়মিত লিখতেন। বর্তমানে কবি যুক্তরাষ্ট্রে মেয়ের সাথে বসবাস করেন। অবসর কাটান বই পড়ে, বাগান করে ও লেখালেখি করে।', NULL, 1, 4, NULL, NULL),
(39, 181, 'অনন্যা জান্নাত', '--QVVZH', 'no address', 'কবিতার অন্তরে যে আহত ও ক্ষতবিক্ষত যন্ত্রণার দীর্ঘশ্বাস নিবিড় আবেগে পুঞ্জীভূত ঘনঘোর সৃষ্টি করে, তার উপশমে শব্দবন্ধ কিংবা অক্ষরের বৃত্ত কি কোনো কাজে লাগে? নাকি একাকী বেদনায় সিক্ত একটি হৃদয় অপ্রাপ্তির ভারে কেবলই অতলে নিমজ্জিত হতে থাকে, যা কখনো তাকে অভিভূত নিশ্বাসের জগতে ফিরিয়ে নিতে পারে না? অনন্যা জান্নাত তার কবিতার অতলগহনে অনুরাগী যে আর্তনাদ তুলে ধরেন, তাতে কখনো কখনো আত্মা বিদীর্ণ হয়, কখনো অনিবার্য নিয়তির কাছে পরাজয় মেনেও তিনি বিশ্বাসের স্থবির জগতটাকে জাগিয়ে তোলার চেষ্টা করেন। তাই অনন্যার কবিতা কখনো আবেগের অশ্রুতে সিক্ত, কখনো হাহাকারের আকুলতায় সমর্পিত। একজন প্রেমিকার জীবনের অপ্রাপ্তির দুর্বহ ভার তাকে যে কতটা ছিন্নভিন্ন করে দিতে পারে, অনন্যার কবিতার পরতে পরতে তার সাক্ষ্য সহজ-সাবলীলতায় দৃষ্টি ও মনের অগোচর থাকে না। অনন্যার কাব্যভাষ্য এবং তার নির্মাণকলায় আমরা যাকে দেখতে পাই, সে প্রেমপূজারি একজন দয়িতা, অপ্রাপ্তির অনিশ্চয়তার মধ্যেও যার আকাক্সক্ষার কখনো মৃত্যু হয় না। অনন্যার কাব্যগ্রন্থ ‘নাকফুলে আদিম ঘ্রাণ’ এর প্রতিটি কবিতার পঙক্তিতে পঙক্তিতে সেই নিষ্কলুষ প্রেমের আকুল আকুতি ব্যক্ত হয়েছে। কাব্যগ্রন্থের প্রতিটি কবিতা পাঠ করতে করতে পাঠককে মিশে যেতে হবে তার অন্তর্গূঢ় আবেদনের মহিমার সঙ্গে। আর এখানেই অনন্যা জান্নাতের কবিতার সার্থকতা।', NULL, 1, 4, NULL, NULL),
(40, 182, 'হিমেল রিফাত', '--WtfUc', 'no address', 'আমি আমাকে লিখেছি,\r\nলিখেছি সমাজ আর দেশকে\r\nহতাশায়, প্রত্যাশায়।\r\nলিখেছি আমাকে সবাতে, সবাইকে আমাতে\r\nপ্রত্যয়ী আলোময় ছন্দে-কবিতায়,\r\nকবিতার ভাষায়।\r\nবেদনাশ্রুতে করেছি স্নান\r\nআবেগের শীর্ষানুভূতি এনে,\r\nবইয়ের গন্ধ মেখে গায়ে\r\nসাহিত্যের শোভা ছড়াইয়ে\r\nকবিতার ঘ্রাণ শুঁকে আকুল হোক মনপ্রাণ।\r\nসমাজ-রাষ্ট্রের অনিয়মের শোক টেনে\r\nকবিতায় হই মত্ত,\r\nজীবন গল্পে যেন কবিতাই অমর সত্য।\r\n‘ক্লেশে গাঁথা নীল চিরকুট’ আমার\r\nপ্রথম কাব্যগ্রন্থ’, রেখে দিতে চাই অনুভূতির চিহ্ন,\r\nআবেগ মিশ্রিত ভালোবাসার।\r\nসকলেই রবে মোর মঙ্গল কামনায়\r\nআমাকেও রেখো তোমরা\r\nহস্তোত্তোলিত মোনাজাতে, দোয়ায়-প্রার্থনায়।', NULL, 1, 4, NULL, NULL),
(41, 183, 'তানভীর সিকদার', '--LjIO8', 'চট্টগ্রামে।', 'তানভীর সিকদার। তরুণ কবি ও বাচিকশিল্পী। জন্মেছেন সমুদ্র-পাহাড়ের অপূর্ব মোহনা চট্টগ্রামে। তার দারুণ দারুণ সব কবিতা ইতোমধ্যে পাঠকের নজর কেড়েছে। তানভীর সিকদার এর কবিতার মূল্যায়ন এবং প্রশংসা করেছেন বিখ্যাত কবি আল মাহমুদ নিজেই। মানসম্পন্ন লেখার দিকে বিশেষ নজর বলেই, এখন পর্যন্ত প্রকাশিত বইয়ের সংখ্যা মাত্র ক’টি। তরুণ এই কবির অনিন্দ্য সুন্দর শব্দগাঁথুনি এবং বিশুদ্ধ উচ্চারণ ভঙ্গিমায় করা আবৃত্তি পাঠকপ্রাণে মুগ্ধতা ছড়ায়।', NULL, 1, 4, NULL, NULL),
